### `src/services/TicketService.ts`

```typescript
import { Repository, Between } from 'typeorm';
import { Ticket, TicketStatus } from '../entities/Ticket';
import { AppDataSource } from '../config/database';
import { CreateTicketDTO } from '../dtos/CreateTicketDTO';
import { UpdateTicketDTO } from '../dtos/UpdateTicketDTO';

export class TicketService {
  private ticketRepository: Repository<Ticket>;

  constructor() {
    this.ticketRepository = AppDataSource.getRepository(Ticket);
  }

  /**
   * Regra: Abertura de chamado permitida só entre 9h e 18h
   */
  private validateBusinessHours(): void {
    const now = new Date();
    const hour = now.getHours();

    if (hour < 9 || hour >= 18) {
      throw new Error('Chamados só podem ser abertos entre 9h e 18h');
    }
  }

  /**
   * Regra: No máximo 10 chamados por dia para cada usuário
   */
  private async validateDailyLimit(userId: string): Promise<void> {
    const now = new Date();
    const startOfDay = new Date(now.getFullYear(), now.getMonth(), now.getDate(), 0, 0, 0);
    const endOfDay = new Date(now.getFullYear(), now.getMonth(), now.getDate(), 23, 59, 59);

    const dailyTickets = await this.ticketRepository.count({
      where: {
        userId,
        createdAt: Between(startOfDay, endOfDay)
      }
    });

    if (dailyTickets >= 10) {
      throw new Error('Limite diário de 10 chamados atingido');
    }
  }

  async create(userId: string, data: CreateTicketDTO): Promise<Ticket> {
    // Valida horário comercial
    this.validateBusinessHours();

    // Valida limite diário
    await this.validateDailyLimit(userId);

    const ticket = this.ticketRepository.create({
      ...data,
      userId,
      status: TicketStatus.OPEN
    });

    return await this.ticketRepository.save(ticket);
  }

  async findAll(userId: string): Promise<Ticket[]> {
    return await this.ticketRepository.find({
      where: { userId },
      order: { createdAt: 'DESC' }
    });
  }

  async findById(id: string, userId: string): Promise<Ticket | null> {
    return await this.ticketRepository.findOne({
      where: { id, userId }
    });
  }

  async update(id: string, userId: string, data: UpdateTicketDTO): Promise<Ticket> {
    const ticket = await this.ticketRepository.findOne({
      where: { id, userId }
    });

    if (!ticket) {
      throw new Error('Chamado não encontrado');
    }

    Object.assign(ticket, data);
    return await this.ticketRepository.save(ticket);
  }

  async delete(id: string, userId: string): Promise<void> {
    const ticket = await this.ticketRepository.findOne({
      where: { id, userId }
    });

    if (!ticket) {
      throw new Error('Chamado não encontrado');
    }

    await this.ticketRepository.remove(ticket);
  }
}
