import { Repository, Between } from 'typeorm';
import { AppDataSource } from '../config/database';
import { Ticket, TicketStatus } from '../entities/Ticket';
import { CreateTicketDto } from '../dtos/CreateTicketDto';
import { UpdateTicketDto } from '../dtos/UpdateTicketDto';
import { AppError } from '../utils/AppError';

export class TicketService {
  private ticketRepository: Repository<Ticket>;

  constructor() {
    this.ticketRepository = AppDataSource.getRepository(Ticket);
  }

  /**
   * Regra de negócio: Máximo 10 chamados por dia por usuário
   */
  private async validateDailyLimit(userId: string): Promise<void> {
    const today = new Date();
    const startOfDay = new Date(today.getFullYear(), today.getMonth(), today.getDate(), 0, 0, 0);
    const endOfDay = new Date(today.getFullYear(), today.getMonth(), today.getDate(), 23, 59, 59);

    const dailyCount = await this.ticketRepository.count({
      where: {
        userId,
        createdAt: Between(startOfDay, endOfDay),
      },
    });

    if (dailyCount >= 10) {
      throw new AppError(
        'Limite diário de 10 chamados atingido. Tente novamente amanhã.',
        429
      );
    }
  }

  /**
   * Regra de negócio: Abertura permitida apenas entre 9h e 18h
   */
  private validateBusinessHours(): void {
    const now = new Date();
    const currentHour = now.getHours();

    // Horário comercial: 9h às 18h (9-18)
    if (currentHour < 9 || currentHour >= 18) {
      throw new AppError(
        'Chamados só podem ser abertos em horário comercial (9h às 18h).',
        403
      );
    }
  }

  async create(userId: string, dto: CreateTicketDto): Promise<Ticket> {
    // Valida regras de negócio
    this.validateBusinessHours();
    await this.validateDailyLimit(userId);

    const ticket = this.ticketRepository.create({
      ...dto,
      userId,
      status: TicketStatus.OPEN,
    });

    return this.ticketRepository.save(ticket);
  }

  async findAll(userId: string, isAdmin: boolean): Promise<Ticket[]> {
    // Admin vê todos, usuário comum vê apenas os seus
    const where = isAdmin ? {} : { userId };

    return this.ticketRepository.find({
      where,
      relations: ['user'],
      order: { createdAt: 'DESC' },
    });
  }

  async findById(id: string, userId: string, isAdmin: boolean): Promise<Ticket> {
    const ticket = await this.ticketRepository.findOne({
      where: { id },
      relations: ['user'],
    });

    if (!ticket) {
      throw new AppError('Chamado não encontrado', 404);
    }

    // Usuário comum só pode ver seus próprios chamados
    if (!isAdmin && ticket.userId !== userId) {
      throw new AppError('Acesso negado a este chamado', 403);
    }

    return ticket;
  }

  async update(
    id: string,
    userId: string,
    isAdmin: boolean,
    dto: UpdateTicketDto
  ): Promise<Ticket> {
    const ticket = await this.findById(id, userId, isAdmin);

    // Usuário comum só pode atualizar seus próprios chamados em aberto
    if (!isAdmin) {
      if (ticket.userId !== userId) {
        throw new AppError('Acesso negado a este chamado', 403);
      }
      // Usuário comum não pode alterar status (apenas admin)
      if (dto.status) {
        throw new AppError('Apenas administradores podem alterar o status', 403);
      }
    }

    Object.assign(ticket, dto);
    return this.ticketRepository.save(ticket);
  }

  async delete(id: string, userId: string, isAdmin: boolean): Promise<void> {
    const ticket = await this.findById(id, userId, isAdmin);

    // Apenas admin ou o próprio criador pode excluir
    if (!isAdmin && ticket.userId !== userId) {
      throw new AppError('Acesso negado a este chamado', 403);
    }

    // Só permite excluir chamados em aberto
    if (ticket.status !== TicketStatus.OPEN) {
      throw new AppError(
        'Não é possível excluir chamados que não estão em aberto',
        400
      );
    }

    await this.ticketRepository.remove(ticket);
  }
}
